mapper.select:
SELECT COUNT(TS.SELL_ID)
FROM TOKEN_SELL TS
LEFT JOIN TOKEN_SELL_TRADE_HISTORY TSTH ON TS.SELL_ID = TSTH.SELL_ID
LEFT JOIN CLIENT C ON TS.CLIENT_ID = C.CLIENT_ID
WHERE
    (
    TSTH.BUYER_WALLET_ADDRESS = #{walletAddress}

        OR TS.STATE IN

            #{item}


    )
    AND TS.VIEW_ROLE = 'TRADER'
    AND TS.SYMBOL = #{symbol}
     AND C.SYMBOL = #{symbol} 
     AND C.CLIENT_CODE = #{clientCode} 
     AND ${keywordType} like '%${keyword}%' 
     AND SELL_TYPE = #{sellType} 


             AND ${timeSearchKey} >= '${searchDateStart}' 


             AND ${timeSearchKey} <= '${searchDateEnd}'

SELECT * FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS ROWNUM , A.* FROM ( SELECT TS.SELL_ID , TS.P_SELL_ID , TS.CLIENT_ID , (SELECT NAME FROM CLIENT WHERE CLIENT_ID = TS.CLIENT_ID) AS CLIENT_NAME , TS.SELL_TYPE , TS.SYMBOL , TS.VIEW_ROLE , TS.SELLER_WALLET_ADDRESS , (SELECT NAME FROM WALLET WHERE WALLET_ADDRESS = TS.SELLER_WALLET_ADDRESS) AS SELLER_NAME , TS.BANK_NAME , TS.BANK_OWNER , TS.BANK_ACCOUNT , TS.QUANTITY , TS.UNIT_PRICE , TS.TOTAL_PRICE , TS.STATE , (SELECT CODE_NAME FROM COMMON_CODE WHERE GROUP_NAME = 'STATE' AND CODE_VALUE=TS.STATE AND LANG_CODE='${langCode}') AS STATE_NAME , TS.DEL_YN , TSTH.TRADE_STATE , (SELECT CODE_NAME FROM COMMON_CODE WHERE GROUP_NAME = 'SELL_TRADE_STATE' AND CODE_VALUE=TSTH.TRADE_STATE AND LANG_CODE='${langCode}') AS TRADE_STATE_NAME , TSTH.BUYER_WALLET_ADDRESS , (SELECT NAME FROM WALLET WHERE WALLET_ADDRESS = TSTH.BUYER_WALLET_ADDRESS) AS BUYER_NAME , TSTH.TXID , TS.TRADE_COMPLETION_DATE , TS.REG_DATE , TS_REF.SELL_ID AS REF_SELL_ID , TS_REF.STATE AS REF_SELL_STATE FROM TOKEN_SELL TS LEFT JOIN TOKEN_SELL_TRADE_HISTORY TSTH ON TS.SELL_ID = TSTH.SELL_ID LEFT JOIN TOKEN_SELL TS_REF ON TS.P_SELL_ID = TS_REF.SELL_ID LEFT JOIN CLIENT C ON TS.CLIENT_ID = C.CLIENT_ID WHERE ( TSTH.BUYER_WALLET_ADDRESS = #{walletAddress}
OR TS.STATE IN
#{item}
) AND TS.VIEW_ROLE = 'TRADER' AND TS.SYMBOL = #{symbol} AND C.SYMBOL = #{symbol} AND C.CLIENT_CODE = #{clientCode} AND ${keywordType} like '%${keyword}%' AND SELL_TYPE = #{sellType}
AND ${timeSearchKey} >= '${searchDateStart}'
AND ${timeSearchKey} <= '${searchDateEnd}'
) A, (SELECT @ROWNUM :=0) TEMP
ORDER BY ${orderByColumn} ${orderByType}
LIMIT ${start}, ${limit} ) RESULT
